let saldo=0;document.getElementById("loginForm").addEventListener("submit",(function(e){e.preventDefault();var t=document.getElementById("username").value;var n=document.getElementById("password").value;var l=true;if(l){document.getElementById("loginForm").style.display="none";document.getElementById("bankingSystem").style.display="block";document.getElementById("welcomeMessage").innerText="Welcome, "+t+"!";fetchBalance()}else{alert("Login failed. Please check your credentials.")}}));document.getElementById("logout").addEventListener("click",(function(){document.getElementById("loginForm").reset();document.getElementById("loginForm").style.display="block";document.getElementById("bankingSystem").style.display="none"}));document.getElementById("bankForm").addEventListener("submit",(function(e){e.preventDefault();var t=document.getElementById("operation").value;var n=parseFloat(document.getElementById("amount").value);if(t==="deposit"){realizarDeposito(n)}else if(t==="withdraw"){realizarSaque(n)}updateBalance()}));function realizarDeposito(e){if(e>0){saldo+=e;console.log("Deposit:",e);console.log("New balance:",saldo)}}function realizarSaque(e){if(saldo>=e){saldo-=e;console.log("Withdraw:",e);console.log("New balance:",saldo)}else{console.log("Insufficient balance")}}function updateBalance(){document.getElementById("balance").innerText="$"+saldo.toFixed(2)}function loadBalance(){fetchBalanceLocally()}function fetchBalanceLocally(){saldo=Math.random()*1e4;updateBalance()}