(function(){const e=document.getElementById("accountForm");e.addEventListener("submit",(function(e){e.preventDefault();const t=document.getElementById("username").value;const s=document.getElementById("passw").value;const n=JSON.parse(localStorage.getItem("registeredUsers"))||[];const o=n.some((e=>e.username===t));if(o){alert("Username already exists. Please choose a different one.")}else{const e={username:t,password:s};n.push(e);localStorage.setItem("registeredUsers",JSON.stringify(n));alert("Registration successful! You can now log in.");const o=window.location.pathname;if(o.includes("register")){}else{window.location.href="./login.html"}}}))})();